  --grid-gap: 20px;
  --container-width: 1200px;
}

.grid-item {
  --columns-span: 4;
  width: calc(
    (var(--container-width) - (var(--grid-columns) - 1) * var(--grid-gap)) 
    / var(--grid-columns) 
    * var(--columns-span)
    + (var(--columns-span) - 1) * var(--grid-gap)
