  /* Variations */
  --primary-light: hsl(var(--primary-h), var(--primary-s), calc(var(--primary-l) + 20%));
  --primary-dark: hsl(var(--primary-h), var(--primary-s), calc(var(--primary-l) - 20%));
  --primary-alpha: hsla(var(--primary-h), var(--primary-s), var(--primary-l), 0.2);
